function strReverse(text){return text.split('').reverse().join('')}function strShuffle(text){var a=text.split(''),n=a.length;for(var i=n-1;i>0;i--){var j=Math.floor(Math.random()*(i+1)),tmp=a[i];a[i]=a[j];a[j]=tmp}return a.join('')}function strCapitalize(text){return text.replace(/^[a-z]/gm,function(c){return c.toUpperCase()})}